import React, { Component } from 'react'
import './App.css'

export default class App extends Component {
  constructor(props){
    super(props)
    this.state={
      name:"Ankit",
      age:24,
      gender:"Male",
    }
  }

  handleClick = () =>{
    alert("Hello")
  }

  render() {
    return (
      <div>
          <button onChange={handleClick()}>Hello</button>
          <h1>Name: {this.state.name}</h1>
          <h2>Age: {this.state.age}</h2>
          <h3>Gender: {this.state.gender}</h3>
      </div>
    )
  }
}
