import React, { Component } from 'react'

import HotelInformation from '../static/Data.js'
export default class Cardsheet extends Component {
    constructor(props){
        super(props)
        this.state={
            flag:false,
            onestar_flag: false,
            twotar_flag: false,
            threetar_flag: false,
            fourstar_flag: false,
        }
    }
    showall = ()=>{
        this.setState({
            flag:true
        })
    }
    render() {
        return (
             <div class="card_bg ">
                 <div class="main_btn">
                    <button class="btn" onClick={this.showall}>All Hotel's</button>
                    <button class="btn" >Hotel above 1*</button>
                    <button class="btn">Hotel above 2*</button>
                    <button class="btn">Hotel above 3*</button>
                    <button class="btn">Hotel above 4*</button>
                 </div>
                {
                    this.state.flag ? 
                    <div class="sequence">
                    <>
                    {
                        HotelInformation.map((e) => {
                            return(
                                <div class="card">
                                    <div class="card_body">
                                        <div>
                                            <img class="img" src={e.image}></img>
                                        </div>
                                        <div>
                                            <h2>{e.title}</h2>
                                            <p>{e.address}</p>
                                            <p>{e.price}</p>
                                            <p>{e.Payment}</p>
                                        </div>
                                        <div class="text">
                                            <button class="btn">{e.start}</button>
                                            <p>{e.votes}</p>
                                            <p>{e.Reviews}</p>
                                        </div>
                                    </div>
                                    <hr></hr>
                                    <div class="card_fotter">
                                        <h4 class="text">{e.mode}</h4>
                                    </div>
                                </div>
                            )
                        })

                    }
                    </>
                </div> : null
                }
            </div>
        )
    }
}
