import React, { Component } from 'react'
import styles from './Todo.module.css'


export default class Todo extends Component {

    constructor(props){
        super(props)
        this.state={
            data: "",
            all_item : [],
            complete_item :[],
            delete_item :[],
            complete_data: [],
            complete_task:''
        }
    }

    handleChange = (e) => {
        this.setState({
        [e.target.name]: e.target.value  
        })
    }
    add = () =>{
       this.state.all_item.push(this.state.data)
        this.setState({})
    }
    handleCheck = (e) =>{
        e.preventDefault()
        this.setState({
            complete_task:e.target.value
        })

    }
    complete = ()=>{
        // this.state.complete_data.push(this.state.complete_task)
        // this.state.all_item.map((e)=>{
        //     this.state.complete_data.map((f)=>{

        //         if (e == f) {
        //             let index = this.state.all_item.indexOf(e)
        //             var x = this.state.all_item.splice(index, 1);
        //             this.state.complete_item.push(x)
        //             this.setState({})
        //         }
        //     })
        // })
         this.setState = () => {
            complete_item.push(this.state.complete_task)
         }
    }
    render() {

        // console.log(this.state.complete_task)
        // console.log(this.state.complete_item)
        return (
            <div className={styles.body}>
                <center>
                    <div className={styles.todo_body_head}>
                        <div className={styles.arrange_input}>
                            <input name="data" placeholder="+ Add your To..Do" className={styles.add_input} onChange={this.handleChange}></input>
                            <button className={styles.add_button} onClick={this.add}>Add</button>
                        </div>
                    </div>
                    <div className={styles.todo_body}>
                        {   
                            this.state.all_item.length > 0 ?
                            this.state.all_item.map((e) => {
                                return(
                                    <div className={styles.list_todo}>
                                        <input name="complete_task" type="checkbox" value={e} onChange={this.handleCheck}></input> 
                                        <p>{e}</p> 
                                    </div>
                                )
                            }) : null
                        }
                    </div>
                    <div className={styles.task_operation}>
                        <div className={styles.arrange_input}>
                            <button className={styles.arrange_button} onClick={this.complete}>complete Task</button>
                            <button className={styles.arrange_button}>Delete Task</button>
                        </div>
                    </div>
                </center>
            </div>
        )
    }
}
