import React, { Component } from 'react'
import styles from './Todo.module.css'


export default class Todo extends Component {

    constructor(props){
        super(props)
        this.state={
            data: "",
            item : []
        }
    }

  handleChange = (e) => {
    this.setState({
        [e.target.name]: e.target.value

    })
}
add = () =>{
    this.state.item.push(e.target.value)
}
    render() {
        console.log(this.state.item, " THis is array")
        return (
            <div className={styles.body}>
                <center>
                    <div className={styles.todo_body}>
                        <div className={styles.arrange_input}>
                            <input name="data" placeholder="+ Add your To..Do" className={styles.add_input} onChange={this.handleChange}></input>
                            <button className={styles.add_button} onClick={this.add}>Add</button>
                        </div>
                        <div className={styles.list_todo}>
                            <input type="checkbox" id="containt"></input>
                            <p>Hello</p>
                        </div>
                    </div>
                </center>
            </div>
        )
    }
}
