import React, { Component } from 'react'
import styles from './Todo.module.css'


export default class Todo extends Component {

    constructor(props){
        super(props)
        this.state={
            data: "",
            all_item : [],
            complete_item :[],
            delete_item :[]
        }
    }

    handleChange = (e) => {
        this.setState({
        [e.target.name]: e.target.value  
        })
    }
    add = () =>{
       this.state.all_item.push(this.state.data)
        this.setState({})
    }
    complete = () =>{
        this.state.all_item.map((a)=>{
            let item = document.getElementById(a).value
            if(a==item){
                this.state.a.pull(this.state.all_item)
                this.state.a.push(this.state.complete_item)
                this.setState({})
            }
        })
    }
    render() {
        return (
            <div className={styles.body}>
                <center>
                    <div className={styles.todo_body_head}>
                        <div className={styles.arrange_input}>
                            <input name="data" placeholder="+ Add your To..Do" className={styles.add_input} onChange={this.handleChange}></input>
                            <button className={styles.add_button} onClick={this.add}>Add</button>
                        </div>
                    </div>
                    <div className={styles.todo_body}>
                        {   
                            this.state.all_item.length > 0 ?
                            this.state.all_item.map((e) => {
                                return(
                                    <div className={styles.list_todo}>
                                        <input type="checkbox" id={e}></input> 
                                        <p>{e}</p> 
                                    </div>
                                )
                            }) : null
                        }
                    </div>
                    <div className={styles.task_operation}>
                        <div className={styles.arrange_input}>
                            <button className={styles.arrange_button} onClick={this.complete}>complete Task</button>
                            <button className={styles.arrange_button}>Delete Task</button>
                        </div>
                    </div>
                </center>
            </div>
        )
    }
}
